import React,{useState} from "react";
import '../../global.css';

function Deployments() {
    const [deployment, setDeployment] = useState('');
    const [newInputDate, setNewInputDate] = useState('');
    const [newInputTime, setNewInputTime] = useState('');
    const [deployments, setDeployments] = useState([])
    //disable the add Deployment button when an input is empty
    const enabled = newInputDate.length > 0 && 
                    newInputTime.length > 0

    const styleEnabled = {
        backgroundColor: '#008CBA',/* blue */
        border: '2px solid black',
        color: 'white',
        padding: '12px',
        textAlign: 'center',
        display: 'block',
        fontSize: '16px',
        marginTop: '15px',
        cursor: 'pointer',
        marginBottom: '15px'   
    } 
    const styleDisabled = {
        border: '#999999',
        backgroundColor: '#cccccc',
        color: '#666666'
    } 

    function inputHandlerDate({target: {value}}) {

        setNewInputDate(value);
      }

      function inputHandlerTime({target: {value}}) {

        setNewInputTime(value);
      }

      function deploymentHandler(){

        setDeployments(deployments.concat(newInputDate + " " + newInputTime))

      }
      const deploymentItems = deployments.map((item) => (

            <li role='deployment' key={item}>{item}</li>

      ))
    return(
        <div className='box-container'>
            <h2>Deployments</h2>
            <div role='deploymentList'><ol type="1">{deploymentItems}</ol></div>
            <h3>Deployment</h3>
            <input 
                role="deploymentdate" 
                onChange={inputHandlerDate} 
                className='deploymentDate'
                type='date'
            /> 

            <input 
                role="deploymenttime" 
                onChange={inputHandlerTime} 
                type='time'
            />
            <button 
                key='addDeployment' 
                onClick={deploymentHandler} 
                className='button'
                disabled={!enabled}
                style={newInputDate.length > 0 && 
               newInputTime.length > 0 ? {styleEnabled}: {styleDisabled}}
                >
                Add Deployment
                </button>
        </div>
    )
}

export default Deployments;
